import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {
  doc,
  getDoc,
  collection,
  getDocs,
  addDoc,
  setDoc,
  updateDoc,
  orderBy,
  query,
 
  Timestamp,
} from "firebase/firestore";

import { db } from "./firebase";
import { getAuth } from "./firebase";
import "./ProductPage.css";

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");
  const [reviews, setReviews] = useState([]);
  const [averageRating, setAverageRating] = useState(0);
  const [inCart, setInCart] = useState(false);

  const auth = getAuth();
  const user = auth.currentUser;
  const userId = user?.uid;
  const userName = user?.displayName || user?.email || "Anonymous";

  useEffect(() => {
    if (!id) return;

  const fetchProduct = async () => {
  try {
    const productRef = doc(db, "products", id);
    const docSnap = await getDoc(productRef);

    if (docSnap.exists()) {
      setProduct({ id: docSnap.id, ...docSnap.data() });
    } else {
      console.error("Product not found in Firestore.");
      setProduct(false);
    }
  } catch (err) {
    console.error("Error fetching product:", err);
    setProduct(false);
  }
};

const fetchReviews = async () => {
  try {
    const productRef = doc(db, "products", id); // Ensure this is a valid DocumentReference
    const reviewsRef = collection(db, "products", id, "reviews");
 // This should work if productRef is valid
    const snap = await getDocs(query(reviewsRef, orderBy("createdAt", "desc")));

    const data = snap.docs.map((d) => d.data());
    setReviews(data);

    const avg = data.length
      ? (data.reduce((a, r) => a + r.rating, 0) / data.length).toFixed(1)
      : 0;
    setAverageRating(avg);
  } catch (err) {
    console.error("Error fetching reviews:", err);
  }
};


    const checkInCart = async () => {
      if (!userId) return;
      try {
        const cartDoc = await getDoc(doc(db, "cart", userId, "items", id));
        setInCart(cartDoc.exists());
      } catch (err) {
        console.error("Error checking cart:", err);
      }
    };

    fetchProduct();
    fetchReviews();
    checkInCart();
  }, [id, userId]);

  const handleSubmitReview = async () => {
    if (!rating || !comment.trim()) {
      alert("Please provide both rating and comment.");
      return;
    }

    try {
      const productRef = doc(db, "products", id); // FIXED
      const reviewRef = collection(productRef, "reviews"); // FIXED

      await addDoc(reviewRef, {
        rating,
        comment: comment.trim(),
        userId,
        userName,
        createdAt: Timestamp.now(),
      });

      setRating(0);
      setComment("");

      const snap = await getDocs(query(reviewRef, orderBy("createdAt", "desc")));
      const data = snap.docs.map((d) => d.data());
      setReviews(data);
      setAverageRating(
        (data.reduce((a, r) => a + r.rating, 0) / data.length).toFixed(1)
      );
    } catch (err) {
      console.error("Error submitting review:", err);
    }
  };

  const handleAddToCart = async () => {
    if (!userId) {
      alert("Please login to add to cart.");
      return;
    }

    try {
      const itemRef = doc(db, "cart", userId, "items", id);
      const docSnap = await getDoc(itemRef);

      if (docSnap.exists()) {
        await updateDoc(itemRef, { quantity: docSnap.data().quantity + 1 });
      } else {
        await setDoc(itemRef, {
          name: product.name,
          price: product.price,
          imageurl: product.imageurl,
          quantity: 1,
          addedAt: Timestamp.now(),
        });
      }
      setInCart(true);
    } catch (err) {
      console.error("Error adding to cart:", err);
    }
  };

  if (product === null) return <p className="loading">Loading...</p>;
  if (product === false) return <p className="error">❌ Product not found.</p>;

  return (
    <div className="product-page">
      <div className="top">
        <img src={product.imageurl} alt={product.name} className="main-image" />
        <div className="info">
          <h1>{product.name}</h1>
          <p className="price">₹{product.price}</p>
          <p className="desc">{product.description}</p>
          <p className="avg">⭐ {averageRating} ({reviews.length})</p>
          <button
            className={`cart-btn ${inCart ? "in-cart" : ""}`}
            onClick={handleAddToCart}
            disabled={inCart}
          >
            {inCart ? "✅ In Cart" : "Add to Cart"}
          </button>
        </div>
      </div>

      <div className="reviews-section">
        <h2>Leave a Review</h2>
        <div className="stars">
          {[1, 2, 3, 4, 5].map((s) => (
            <span
              key={s}
              onClick={() => setRating(s)}
              style={{ color: s <= rating ? "#FFD700" : "#ccc", cursor: "pointer" }}
            >
              ★
            </span>
          ))}
        </div>
        <textarea
          rows="4"
          value={comment}
          placeholder="Write your review"
          onChange={(e) => setComment(e.target.value)}
        ></textarea>
        <button className="submit-btn" onClick={handleSubmitReview}>
          Submit Review
        </button>
      </div>

      <div className="existing-reviews">
        <h2>Customer Reviews</h2>
        {reviews.length === 0 && <p>No reviews yet.</p>}
        {reviews.map((r, i) => (
          <div key={i} className="review-card">
            <strong>{r.userName}</strong>
            <span>{"★".repeat(r.rating) + "☆".repeat(5 - r.rating)}</span>
            <p>{r.comment}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
