  const [carouselIndex, setCarouselIndex] = useState(0);
  const carouselTrackRef = useRef(null);
  const trendingTrackRef = useRef(null);

  const moveCarousel = (direction) => {
    const items = document.querySelectorAll('.carousel-item');
    const visibleItems = window.innerWidth <= 480 ? 1 : window.innerWidth <= 768 ? 2 : 4;
    const itemCount = items.length;
    let newIndex = carouselIndex + direction;
    if (newIndex < 0) newIndex = 0;
    if (newIndex > itemCount - visibleItems) newIndex = itemCount - visibleItems;
    setCarouselIndex(newIndex);
  };

  useEffect(() => {
    const items = document.querySelectorAll('.carousel-item');
    const itemWidth = items[0]?.offsetWidth + 20;
    carouselTrackRef.current.style.transform = `translateX(-${carouselIndex * itemWidth}px)`;
  }, [carouselIndex]);

  useEffect(() => {
    const interval = setInterval(() => {
      const items = document.querySelectorAll('.carousel-item');
      const visibleItems = window.innerWidth <= 480 ? 1 : window.innerWidth <= 768 ? 2 : 4;
      setCarouselIndex((prev) => (prev + 1) % (items.length - visibleItems + 1));
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  const scrollTrending = (direction) => {
    if (trendingTrackRef.current) {
      trendingTrackRef.current.scrollLeft += direction * 270;
    }
  };

  const [showContact, setShowContact] = useState(false);
  const [showProducts, setShowProducts] = useState(false);

  return (
    <>
      <div className="box">
        <img src="/image.jpg" alt="Logo" />

        <div className="search-bar">
          <input type="text" placeholder="Search royal attires..." />
          <i className="fas fa-search"></i>
        </div>

        <div className="menu">
          <a href="#">Home</a>

          <div className="product-wrapper">
            <a href="#" onClick={(e) => { e.preventDefault(); setShowProducts(!showProducts); }}>
              Products
            </a>
            {showProducts && (
              <div className="product-dropdown show">
                <a href="#">Suits</a>
                <a href="#">Kurtis</a>
                <a href="#">Cord sets</a>
                <a href="#">Indo-Western</a>
                <a href="#">Premium attires</a>
              </div>
            )}
          </div>

          <div className="contact-wrapper">
            <a href="#" onClick={(e) => { e.preventDefault(); setShowContact(!showContact); }}>Contact</a>
            {showContact && (
              <div className="contact-dropdown">
                <p>Email: client@example.com</p>
                <p>Phone: +91-9876543210</p>
              </div>
            )}
          </div>

          <a href="/signup">Sign Up</a>
        </div>
      </div>

      <div className="hero">
        <div className="hero-content">
          <h1>Welcome to Param Vastra</h1>
          <p>
            Discover the elegance of royalty. Our handcrafted ethnic and fusion attires bring timeless tradition
            with a modern twist, tailored just for you.
          </p>
        </div>
      </div>

      <div className="carousel-container">
        <div className="carousel-track" ref={carouselTrackRef}>
          {[1, 2, 3, 4, 5].map((item, idx) => (
            <div className="carousel-item" key={idx}>
              <img src="/image.jpg" alt={`Item ${item}`} />
              <div className="product-info">
                <h3>Royal Peach Suit</h3>
                <p>₹2,499</p>
              </div>
            </div>
          ))}
        </div>
        <div className="carousel-arrow left" onClick={() => moveCarousel(-1)}>❮</div>
        <div className="carousel-arrow right" onClick={() => moveCarousel(1)}>❯</div>
      </div>

      <div className="video-section">
        <div className="video-box">
          <video src="/video.mp4" autoPlay muted loop></video>
        </div>
        <div className="video-box">
          <video src="/video.mp4" autoPlay muted loop></video>
        </div>
      </div>

      <div className="trending-section">
        <h2>Trending in Our Shop</h2>
        <div className="trending-container">
          <button className="arrow left" onClick={() => scrollTrending(-1)}>❮</button>
          <div className="trending-track" ref={trendingTrackRef}>
            {[...Array(6)].map((_, idx) => (
              <div className="trending-item" key={idx}>
                <img src="/image.jpg" alt={`Trending ${idx + 1}`} />
              </div>
            ))}
          </div>
          <button className="arrow right" onClick={() => scrollTrending(1)}>❯</button>
        </div>
      </div>

      <div className="signup-section">
        <h2>Log in for personalised experience</h2>
        <button className="signup-btn" onClick={() => (window.location.href = '/login')}>Log in</button>
        <div className="new-customer">
          <p>If you are a new customer then</p>
          <button className="start-btn" onClick={() => (window.location.href = '/signup')}>Start Here</button>
        </div>
      </div>

      <footer className="footer">
        <div className="footer-container">
          <div className="footer-section">
            <img src="/image.jpg" alt="Param Vastra Logo" className="footer-logo" />
            <p>123 Royal Street, Jaipur, Rajasthan, India</p>
            <div className="social-icons">
              <a href="#"><i className="fab fa-facebook-f"></i></a>
              <a href="#"><i className="fab fa-instagram"></i></a>
              <a href="#"><i className="fab fa-twitter"></i></a>
              <a href="#"><i className="fab fa-youtube"></i></a>
              <a href="#"><i className="fab fa-pinterest"></i></a>
            </div>
          </div>

          <div className="footer-section">
            <h4>Support</h4>
            <ul>
              <li><a href="#">Email Us</a></li>
              <li><a href="#">About Page</a></li>
              <li><a href="#">See Guide</a></li>
              <li><a href="#">Shipping</a></li>
              <li><a href="#">Returns</a></li>
              <li><a href="#">Privacy Policy</a></li>
            </ul>
          </div>

          <div className="footer-section">
            <h4>Shop</h4>
            <ul>
              <li><a href="/signup">Women Shopping</a></li>
              <li><a href="/signup">Suits</a></li>
              <li><a href="/signup">Kurtis</a></li>
              <li><a href="/signup">Indo Western</a></li>
              <li><a href="/signup">Premium Attires</a></li>
            </ul>
          </div>
        </div>

        <div className="footer-bottom">
          <p>© 2025 All rights reserved | Designed by Param Vastra</p>
        </div>
      </footer>
    </>
  );
};

export default LandingPage;
