import React, { useEffect, useState } from "react";
import {
  db,
  collection,
  getDocs,
  query,
  addDoc,
  doc,
  setDoc,
  where,
} from "./firebase";
import "./home.css";

const Home = () => {
  const [products, setProducts] = useState([]);
  const [userCartItems, setUserCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const userId = localStorage.getItem("uid");

  useEffect(() => {
    if (!userId) alert("You're not logged in! Please log in to add to cart.");
    loadProducts();
  }, []);

  const loadProducts = async () => {
    const snapshot = await getDocs(collection(db, "products"));
    const loaded = snapshot.docs.map((doc) => doc.data());
    setProducts(loaded);
    setUserCartItems(await getUserCartItems());
  };

  const getUserCartItems = async () => {
    if (!userId) return [];
    const cartRef = collection(db, "cart", userId, "items");
    const snapshot = await getDocs(cartRef);
    return snapshot.docs.map((doc) => doc.data().name);
  };

  const saveSearchTerm = (term) => {
    const key = `search_history_${userId}`;
    const history = JSON.parse(localStorage.getItem(key)) || [];
    if (!history.includes(term)) {
      history.unshift(term);
      if (history.length > 10) history.pop();
      localStorage.setItem(key, JSON.stringify(history));
    }
  };

  const searchProducts = async (term) => {
    const lowerTerm = term.trim().toLowerCase();
    if (lowerTerm.length < 2) {
      loadProducts();
      return;
    }

    saveSearchTerm(lowerTerm);
    const snapshot = await getDocs(query(collection(db, "products")));
    const matched = snapshot.docs
      .map((doc) => doc.data())
      .filter((product) => product.name?.toLowerCase().includes(lowerTerm));
    setProducts(matched);
    setSuggestions([]);
  };

  const handleAddToCart = async (product) => {
    if (!userId) {
      alert("Please log in to add to cart.");
      return;
    }

    const cartRef = collection(db, "cart", userId, "items");
    const q = query(cartRef, where("name", "==", product.name));
    const snapshot = await getDocs(q);

    if (!snapshot.empty) {
      const docRef = snapshot.docs[0];
      const existing = docRef.data();
      const quantity = (existing.quantity || 1) + 1;

      await setDoc(doc(db, "cart", userId, "items", docRef.id), {
        ...existing,
        quantity,
        updatedAt: new Date(),
      });
      alert(`ðŸ” ${product.name} quantity updated`);
    } else {
      await addDoc(cartRef, {
        ...product,
        quantity: 1,
        addedAt: new Date(),
      });
      alert(`âœ… ${product.name} added to cart`);
    }

    setUserCartItems(await getUserCartItems());
  };

  const handleInputChange = async (e) => {
    const term = e.target.value.trim().toLowerCase();
    setSearchTerm(term);

    if (term.length < 2) {
      setSuggestions([]);
      return;
    }

    const history = JSON.parse(localStorage.getItem(`search_history_${userId}`)) || [];
    const filteredHistory = history.filter((h) => h.includes(term));

    const snapshot = await getDocs(query(collection(db, "products")));
    const matches = snapshot.docs
      .map((doc) => doc.data().name?.toLowerCase())
      .filter((name) => name && name.includes(term));

    const combined = [...new Set([...filteredHistory, ...matches])].slice(0, 10);
    setSuggestions(combined);
  };

  return (
    <div className="home-container">
      <div className="top-bar">
  <div className="logo">StyleCart ðŸ§¥</div>
  <div className="search-bar">
    <input
      type="text"
      placeholder="Search for clothing..."
      value={searchTerm}
      onChange={handleInputChange}
    />
    {suggestions.length > 0 && (
      <div className="suggestions-box">
        {suggestions.map((s, i) => (
          <div key={i} onClick={() => searchProducts(s)}>
            {s}
          </div>
        ))}
      </div>
    )}
  </div>
  <div className="top-icons">
    <a className="icon-link" href="/cart">ðŸ›’</a>
    <a className="icon-link" href="/profile">ðŸ‘¤</a>
  </div>
</div>

      <div className="products-container">
        {products.length === 0 ? (
          <p>No products found</p>
        ) : (
          products.map((product, index) => {
            const isInCart = userCartItems.includes(product.name);
            return (
              <div className="product-card" key={index}>
                <img src={product.imageurl} alt={product.name} />
                <h3>{product.name}</h3>
                <p>{product.description || "No description available."}</p>
                <strong>â‚¹{product.price}</strong>
                <button
                  className="add-to-cart"
                  onClick={() => handleAddToCart(product)}
                  disabled={isInCart}
                >
                  {isInCart ? "âœ… In Cart" : "Add to Cart ðŸ›’"}
                </button>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default Home;
