import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import {
  doc,
  getDoc,
  collection,
  getDocs,
  addDoc,
  setDoc,
  updateDoc,
  orderBy,
  query,
  Timestamp,
} from "firebase/firestore";
import { db, auth } from "./firebase";
import { onAuthStateChanged } from "firebase/auth";
import "./ProductPage.css";

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");
  const [reviews, setReviews] = useState([]);
  const [averageRating, setAverageRating] = useState(0);
  const [inCart, setInCart] = useState(false);
  const [user, setUser] = useState(null);

  // Listen for auth state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (firebaseUser) => {
      setUser(firebaseUser || null);
    });
    return () => unsubscribe();
  }, []);

  // Fetch product & reviews
  useEffect(() => {
    if (!id) return;

    const fetchProduct = async () => {
      try {
        const productRef = doc("products", id);
        const docSnap = await getDoc(productRef);
        if (docSnap.exists()) {
          setProduct({ id: docSnap.id, ...docSnap.data() });
        } else {
          setProduct(false);
        }
      } catch (err) {
        console.error("Error fetching product:", err.message);
      }
    };

    const fetchReviews = async () => {
      try {
        const reviewsRef = doc("products", id, "reviews");
        const snap = await getDocs(query(reviewsRef, orderBy("createdAt", "desc")));
        const data = snap.docs.map((d) => d.data());
        setReviews(data);

        const avg = data.length
          ? (data.reduce((a, r) => a + r.rating, 0) / data.length).toFixed(1)
          : 0;
        setAverageRating(avg);
      } catch (err) {
        console.error("Error fetching reviews:", err.message);
      }
    };

    fetchProduct();
    fetchReviews();
  }, [id]);

  // Check if item is in cart
  useEffect(() => {
    const checkInCart = async () => {
      if (!user || !id) return;

      try {
        const cartDoc = await getDoc(doc("cart", user.id, "items", id));
        setInCart(cartDoc.exists());
      } catch (err) {
        console.error("Error checking cart:", err.message);
      }
    };

    checkInCart();
  }, [user, id]);

  const handleSubmitReview = async () => {
    if (!rating || !comment.trim()) {
      alert("Please provide both rating and comment.");
      return;
    }

    if (!user) {
      alert("Please log in to submit a review.");
      return;
    }

    try {
      const reviewRef = collection(db, "products", id, "reviews");

      await addDoc(reviewRef, {
        rating,
        comment: comment.trim(),
        userId: user.uid,
        userName: user.displayName || user.email || "Anonymous",
        createdAt: Timestamp.now(),
      });

      // Refresh reviews
      const snap = await getDocs(query(reviewRef, orderBy("createdAt", "desc")));
      const data = snap.docs.map((d) => d.data());
      setReviews(data);
      setAverageRating(
        (data.reduce((a, r) => a + r.rating, 0) / data.length).toFixed(1)
      );

      setRating(0);
      setComment("");
    } catch (err) {
      console.error("Error submitting review:", err.message);
    }
  };

  const handleAddToCart = async () => {
    if (!user) {
      alert("Please log in to add to cart.");
      return;
    }

    try {
      const itemRef = doc(db, "cart", user.uid, "items", id);
      const docSnap = await getDoc(itemRef);

      if (docSnap.exists()) {
        await updateDoc(itemRef, { quantity: docSnap.data().quantity + 1 });
      } else {
        await setDoc(itemRef, {
          name: product.name,
          price: product.price,
          imageurl: product.imageurl,
          quantity: 1,
          addedAt: Timestamp.now(),
        });
      }
      setInCart(true);
    } catch (err) {
      console.error("Error adding to cart:", err.message);
    }
  };

  if (product === null) return <p className="loading">Loading...</p>;
  if (product === false) return <p className="error">❌ Product not found.</p>;

  return (
    <div className="product-page">
      <div className="top">
        <img src={product.imageurl} alt={product.name} className="main-image" />
        <div className="info">
          <h1>{product.name}</h1>
          <p className="price">₹{product.price}</p>
          <p className="desc">{product.description}</p>
          <p className="avg">⭐ {averageRating} ({reviews.length})</p>
          <button
            className={`cart-btn ${inCart ? "in-cart" : ""}`}
            onClick={handleAddToCart}
            disabled={inCart}
          >
            {inCart ? "✅ In Cart" : "Add to Cart"}
          </button>
        </div>
      </div>

      <div className="reviews-section">
        <h2>Leave a Review</h2>
        <div className="stars">
          {[1, 2, 3, 4, 5].map((s) => (
            <span
              key={s}
              onClick={() => setRating(s)}
              style={{ color: s <= rating ? "#FFD700" : "#ccc", cursor: "pointer" }}
            >
              ★
            </span>
          ))}
        </div>
        <textarea
          rows="4"
          value={comment}
          placeholder="Write your review"
          onChange={(e) => setComment(e.target.value)}
        ></textarea>
        <button className="submit-btn" onClick={handleSubmitReview}>
          Submit Review
        </button>
      </div>

      <div className="existing-reviews">
        <h2>Customer Reviews</h2>
        {reviews.length === 0 && <p>No reviews yet.</p>}
        {reviews.map((r, i) => (
          <div key={i} className="review-card">
            <strong>{r.userName}</strong>
            <span>{"★".repeat(r.rating) + "☆".repeat(5 - r.rating)}</span>
            <p>{r.comment}</p>
            <small>{r.createdAt?.toDate().toLocaleString()}</small>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
