import React, { useState, useEffect, useRef } from 'react';
import './LandingPage.css';

const LandingPage = () => {
  const [carouselIndex, setCarouselIndex] = useState(0);
  const [visibleItems, setVisibleItems] = useState(4);
  const carouselTrackRef = useRef(null);
  const trendingTrackRef = useRef(null);
  const [showContact, setShowContact] = useState(false);
  const [showProducts, setShowProducts] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);

  // Determine visible items based on window width
  useEffect(() => {
    const updateVisible = () => {
      if (window.innerWidth <= 480) setVisibleItems(1);
      else if (window.innerWidth <= 768) setVisibleItems(2);
      else setVisibleItems(4);
    };
    updateVisible();
    window.addEventListener('resize', updateVisible);
    return () => window.removeEventListener('resize', updateVisible);
  }, []);

  const moveCarousel = (dir) => {
    const total = 5;
    let newIdx = carouselIndex + dir;
    newIdx = Math.max(0, Math.min(total - visibleItems, newIdx));
    setCarouselIndex(newIdx);
  };

  useEffect(() => {
    const track = carouselTrackRef.current;
    const item = track?.querySelector('.carousel-item');
    const itemW = item ? item.offsetWidth + 20 : 0;
    track.style.transform = `translateX(-${carouselIndex * itemW}px)`;
  }, [carouselIndex, visibleItems]);

  useEffect(() => {
    const intv = setInterval(() => {
      setCarouselIndex((prev) => {
        const max = 5 - visibleItems;
        return prev >= max ? 0 : prev + 1;
      });
    }, 3000);
    return () => clearInterval(intv);
  }, [visibleItems]);

  const scrollTrending = (dir) => {
    if (trendingTrackRef.current) trendingTrackRef.current.scrollLeft += dir * 270;
  };

  const toggleSidebar = () => setSidebarOpen((s) => !s);

  useEffect(() => {
    const handleOut = (e) => {
      if (!e.target.closest('.product-wrapper')) setShowProducts(false);
      if (!e.target.closest('.contact-wrapper')) setShowContact(false);
    };
    document.addEventListener('click', handleOut);
    return () => document.removeEventListener('click', handleOut);
  }, []);

  return (
    <>
      <header className="nav">
        <div className="logo-search">
          <img src="/logo192.png" alt="Logo" className="logo" />
          <div className="search">
            <input type="text" placeholder="Search royal attires..." />
            <i className="fas fa-search" />
          </div>
        </div>

        <nav className="menu">
          <a href="#">Home</a>
          <div className="product-wrapper">
            <a href="#" onClick={(e) => { e.preventDefault(); setShowProducts(!showProducts); }}>Products</a>
            {showProducts && (
              <ul className="dropdown">
                {['Suits', 'Kurtis', 'Cord sets', 'Indo-Western', 'Premium attires'].map((p) => (
                  <li key={p}><a href="#">{p}</a></li>
                ))}
              </ul>
            )}
          </div>
          <div className="contact-wrapper">
            <a href="#" onClick={(e) => { e.preventDefault(); setShowContact(!showContact); }}>Contact</a>
            {showContact && (
              <div className="dropdown contact-dropdown">
                <p>Email: client@example.com</p>
                <p>Phone: +91-9876543210</p>
              </div>
            )}
          </div>
          <a href="/signup" className="btn signup">Sign Up</a>
          <div className="hamburger" onClick={toggleSidebar}><i className="fas fa-bars"></i></div>
        </nav>

        {sidebarOpen && (
          <aside className="sidebar">
            <a href="#">Home</a>
            <a href="#" onClick={(e) => { e.preventDefault(); setShowProducts(!showProducts); }}>Products</a>
            <a href="#" onClick={(e) => { e.preventDefault(); setShowContact(!showContact); }}>Contact</a>
            <a href="/signup">Sign Up</a>
          </aside>
        )}
      </header>

      <section className="hero" style={{ backgroundImage: `url(${bannerImg})` }}>
        <div className="hero-text">
          <h1>Welcome to Bardiya Clothing</h1>
          <p>Discover handcrafted ethnic & fusion wear — where tradition meets elegance.</p>
        </div>
      </section>

      <section className="carousel-container">
        <button className="arrow left" onClick={() => moveCarousel(-1)}>❮</button>
        <div className="carousel-track" ref={carouselTrackRef}>
          {[1, 2, 3, 4, 5].map((i) => (
            <div className="carousel-item" key={i}>
              <img src={`/gallery/item${i}.jpg`} alt={`Item ${i}`} />
              <div className="info">
                <h3>Royal Peach Suit</h3>
                <p>₹2,499</p>
              </div>
            </div>
          ))}
        </div>
        <button className="arrow right" onClick={() => moveCarousel(1)}>❯</button>
      </section>

      <section className="trending">
        <h2>Trending in Our Shop</h2>
        <div className="trending-inner">
          <button onClick={() => scrollTrending(-1)} className="arrow">❮</button>
          <div className="trending-track" ref={trendingTrackRef}>
            {[...Array(6)].map((_, i) => (
              <div className="trending-item" key={i}>
                <img src={`/gallery/trend${i + 1}.jpg`} alt={`Trend ${i + 1}`} />
              </div>
            ))}
          </div>
          <button onClick={() => scrollTrending(1)} className="arrow">❯</button>
        </div>
      </section>

